import React from "react";

export default function QuestionPage({ question, onAnswer, current, total }) {
  return (
    <div className="max-w-xl mx-auto bg-white shadow-xl rounded-xl p-6">
      <h2 className="text-2xl font-bold mb-4">Question {current} of {total}</h2>
      <p className="text-lg font-semibold mb-4">{question.question}</p>
      <div className="space-y-2">
        {question.options.map((option, index) => (
          <button
            key={index}
            className="w-full bg-blue-500 hover:bg-blue-600 text-white p-3 rounded-lg transition"
            onClick={() => onAnswer(option.trait)}
          >
            {option.text}
          </button>
        ))}
      </div>
    </div>
  );
}
