import React from "react";
import { BarChart, Bar, XAxis, YAxis, Tooltip } from "recharts";
import traitInfo from "../utils/traits";

export default function ResultPage({ answers, onRestart }) {
  const traitCount = answers.reduce((acc, trait) => {
    acc[trait] = (acc[trait] || 0) + 1;
    return acc;
  }, {});

  const chartData = Object.entries(traitCount).map(([trait, count]) => ({
    name: trait,
    value: count,
  }));

  const topTrait = chartData.reduce((a, b) => (a.value > b.value ? a : b), {});

  return (
    <div className="max-w-2xl mx-auto bg-white p-6 shadow-2xl rounded-xl">
      <h2 className="text-3xl font-bold mb-4 text-center">Your Character Trait</h2>
      <p className="text-lg text-center mb-6">
        You are most likely <strong>{topTrait.name}</strong> ðŸŽ­ â€” {traitInfo[topTrait.name]}
      </p>
      <BarChart width={400} height={250} data={chartData}>
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Bar dataKey="value" fill="#f97316" />
      </BarChart>
      <div className="text-center mt-6">
        <button
          onClick={onRestart}
          className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-full"
        >
          Retake Quiz ðŸ”„
        </button>
      </div>
    </div>
  );
}
